import React from "react";
import ListaMensagens from "./ListaMensagens";

class Mensagens extends React.Component {
  // mostrarMensagem = () => {
  //   if(this.props.mensagens.length) {
  //     return <p>Você tem {this.props.mensagens.length} mensagens novas </p>
  //   } else {
  //     return <p>Você não tem novas mensagens</p>
  //   }
  // }

  render() {
    console.log("Props", this.props.mensagens);
    const quantidadeMensagens =
      this.props.mensagens && this.props.mensagens.length;
    return (
      <div>
        <h3>Mensagens</h3>
        {quantidadeMensagens ? (
          <p>Você tem {quantidadeMensagens} mensagens novas </p>
        ) : (
          <p>Você não tem novas mensagens</p>
        )}

        {/* {this.mostrarMensagem()} */}
        {this.props.mensagens && (
          <ListaMensagens mensagens={this.props.mensagens} />
        )}
      </div>
    );
  }
}

export default Mensagens;
