import "./styles.css";
import React from "react";
import Home from "./components/Home";
import Login from "./components/Login";

export default class App extends React.Component {
  state = {
    estaLogado: false
  };

  renderizaTelaCorreta = () => {
    if (this.state.estaLogado) {
      return <Home fazerLogout={this.fazerLogout} />;
    } else {
      return <Login fazerLogin={this.fazerLogin} />;
    }
  };

  fazerLogin = () => {
    this.setState({ estaLogado: true });
  };

  fazerLogout = () => {
    this.setState({ estaLogado: false });
  };

  render() {
    return (
      <div className="App">
        <h1>Labook</h1>
        <hr />
        {this.renderizaTelaCorreta()}
      </div>
    );
  }
}
