import React from "react";
import Mensagens from "./Mensagens";
import Posts from "./Posts";

class Home extends React.Component {
  state = {
    telaAtual: "posts",
    mensagens: ["Oi sumida!!!", "Oi amiga, tudo bem?", "Oláaaa"]
  };

  renderizaTelaCorreta = () => {
    switch (this.state.telaAtual) {
      case "posts":
        return <Posts />;
      case "mensagens":
        return <Mensagens mensagens={this.state.mensagens} />;
      default:
        return <p>Erro! Página não encontrada</p>;
    }
  };

  irParaMensagens = () => {
    this.setState({ telaAtual: "mensagens" });
  };

  irParaPosts = () => {
    this.setState({ telaAtual: "posts" });
  };

  render() {
    return (
      <div>
        <h2>Home</h2>
        <button onClick={this.irParaPosts}>Posts</button>
        <button onClick={this.irParaMensagens}>Mensagens</button>
        <button onClick={this.props.fazerLogout}>Logout</button>
        {this.renderizaTelaCorreta()}
      </div>
    );
  }
}

export default Home;
