import React from "react";
import axios from "axios";

const url =
  "https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users";

const headers = {
  headers: {
    Authorization: "luis-mijias-lovelace"
  }
};

export default class App extends React.Component {
  state = {
    name: [],
    email: ""
  };

  componentDidMount() {
    this.pegarNome();
  }

  mudaInputNome = (e) => {
    this.setState({ inputNome: e.target.value });
  };

  pegarNome = () => {
    axios
      .get(url, headers)
      .then((res) => {
        this.setState({ playlists: res });
      })
      .catch((err) => {
        alert(err);
      });
  };

  criarPlaylist = () => {
    const body = {
      name: this.state.inputNome
    };

    axios
      .post(url, body, headers)
      .then((res) => {
        alert("Playlist adicionada com sucesso");
        this.setState({ inputNome: "" });
        this.pegarNome();
      })
      .catch((err) => {
        alert(err.response.data.message);
      });
  };

  render() {
    const componentesPlaylists = this.state.playlists.map((play) => {
      return <li key={play.id}>{play.name}</li>;
    });

    return (
      <div>
        <h1>labenusers</h1>
        <input
          value={this.state.inputNome}
          onChange={this.mudaInputNome}
        />
        <button onClick={this.criarPlaylist}>Enviar</button>
        {componentesPlaylists}
      </div>
    );
  }
}
