import React from "react";
import axios from "axios";



const url =  " https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users" ;



const headers = {  headers: {    Authorization: "luis-mijias-lovelace"  }};


const UserItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

export default class App extends React.Component {
  state = {
    name: [],
    email:[],
    inputNome: "",
    inputEmail:""
  };

  componentDidMount() {
    this.criarUsuario(); 
    this.pegarNome();   

  }

  mudaInputNome = (e) => {
    this.setState({ inputNome: e.target.value });
  };

  mudaInputEmail = (e) => {
    this.setState({ inputEmail: e.target.value });
  };


  pegarNome = () => {
    axios
      .get(url, headers)
      .then((res) => {
        console.log(res)
        this.setState({ name: res.data });
      })
      .catch((err) => {
        alert(err);
      });
  };
  pegarEmail = () => {
    axios
      .get(url, headers)
      .then((res) => {
        console.log(res)
        this.setState({ email: res.data });
      })
      .catch((err) => {
        alert(err);
      });
  };

 criarUsuario = () => {
    const body = {
      name: this.state.inputNome,
      email: this.state.inputEmail     
    };

    axios
      .post(url, body, headers)
      .then((res) => {
        console.log(res)
        alert("UsuÃ¡rio adicionado com sucesso");
        this.setState({ inputNome: "",inputEmail:"" });
        this.pegarNome();
        this.pegarEmail();
      })
      .catch((err) => {
        alert(err.response.data.message);
      });
  };

  getUsersList = () => {
    axios
      .get(
        "https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users",
        {
          headers: {
            Authorization: "luis-mijias-lovelace"
          }
        }
      )
      .then(response => {
        this.setState({ usersList: response.data });
      })
      .catch(error => {
        console.log(error.response);
      });
  };

  onClickDeleteUser = userId => {
    axios
      .delete(
        `https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users/${userId}`,
        {
          headers: {
            Authorization: "luis-mijias-lovelace"
          }
        }
      )
      .then(response => {
        window.alert("Usuario deletado com sucesso");
        this.getUsersList();
      })
      .catch(error => {
        window.alert("Deu um erro :(");
      });
  };


  render() {
    // const componentesNome = this.state.name.map((play) => {
    //   return <li key={play.id}>{play.name}</li>;
    // });

    return (
      <div>

        <h1>labenusers</h1>
        <input
          placeholder={"insira seu nome"}
          value={this.state.inputNome}        
          onChange={this.mudaInputNome}
        />
        <br></br>
        <input
          placeholder={"insira seu e-mail"}
          value={this.state.inputEmail}        
          onChange={this.mudaInputEmail}
        />   
        <button onClick={() => this.onClickDeleteUser(user.id)}>
        Apagar
        </button>

        <button onClick={this.criarUsuario}>Enviar</button>
        {/* {componentesNome} */}

        <div>
          {this.state.usersList.map(user => {
            return (
              <UserItem>
                <p>{user.name}</p>
                <button onClick={() => this.onClickDeleteUser(user.id)}>
                  Apagar
                </button>
              </UserItem>
            );
          })}
        </div>

      </div>
    );
  }
}
