import React from "react";
import axios from "axios";



class Playlists extends React.Component {

    state = {
        playlist: []
    }

    componentDidMount() {
        this.getPlaylists()
    }

    getPlaylists = async () => {
        const url = { url: "https://us-central1-labenu-apis.cloudfunctions.net/labefy/playlists" }
        try {
            const res = await axios.get(url, { headers: { Authorization: "luis-mijias-lovelace" } })
        } catch (e) {
            console.log(e)
        }
    }

    deletePlaylist = async (id) => {
        const urldell = { url: `https://us-central1-labenu-apis.cloudfunctions.net/labefy/playlists/${id}` }
        try {
            const result = window.confirm("Quer excluir o item")
            if (result == true) {
                const res = await axios.delete(urldell, { headers: { Authorization: "luis-mijias-lovelace" } })
                this.getPlaylists()
            }
        }
        catch (err) {
            console.log(err)
        }
    }

    render() {
        const playlist = this.state.playlist.map ((play) => (
            <div key={play.id}>
                <body>
                    {play.name}
                    <button
                    onClick={() => this.deletePlaylist(play.id)}
                    >x</button>
                </body>
            </div>
        ))

    return (
        <div>
            <h2>Playlists Aqui</h2>
            <div>{playlist}</div>
        </div>
    )    
    }






}
export default Playlists
